package main

import "fmt"

func main() {
	fmt.Println(part1(input1))
	fmt.Println(part1(input2))
	fmt.Println(part2(input1))
	fmt.Println(part2(input2))
}

func part1(s string) int {
	return distinctAfter(s, 4)
}

func part2(s string) int {
	return distinctAfter(s, 14)
}

func distinctAfter(s string, n int) int {
	for i := n; i < len(s)+1; i++ {
		if isUnique(s[i-n : i]) {
			return i
		}
	}
	return -1
}

func isUnique(s string) bool {
	m := make(map[rune]bool)
	for _, b := range s {
		if m[rune(b)] {
			return false
		}
		m[rune(b)] = true
	}
	return true
}

